export const getAsset: AssetLookup = (function () {
  const fn = ((id: string) => ASSETS[id]?.src ?? null) as AssetLookup;
  fn.raw = ASSETS;
  fn.has = (id: string) => !!ASSETS[id];
  fn.info = (id: string) => ASSETS[id] ?? null;
  return fn;
})();

export const getGame = (slugOrId: string): string | null => {
  const direct = ASSETS[slugOrId]?.src;
  if (direct) return direct;
  const found = Object.values(ASSETS).find(
    (r) => r.type === 'game' && (r as any).file.endsWith('/' + slugOrId),
  );
  return found ? (found as any).src : null;
};
